<!--html-->
<template>
    <div class="cell-box border-1px"
         :style="{height:height,'line-height':height}">
        <div class="cell-box-title">{{title}}</div>
        <div class="cell-box-des">
            <div v-if="des">{{des}}</div>
        </div>
    </div>

</template>

<!--script-->
<script type="text/ecmascript-6">
    export default {
        props: {
            title: {
                type: String,
                default: "标题"
            },
            des: {
                type: String,
                default: ""
            },
            height: {
                type: String,
                default: "1.28rem"
            }
        },
        mounted() {
            this.setRootFontSize();
            window.onresize = ()=>{
                this.setRootFontSize();
            }
        },
        methods:{
        //设置根元素字体大小
        setRootFontSize(){
            // 进行rem适配 设置根元素字体大小
            let width = document.documentElement.clientWidth || document.body.clientWidth;
            let htmlDom = document.getElementsByTagName("html")[0]
            htmlDom.style.fontSize = width / 10 + 'px';
        }
        }
    }

</script>

<!--css-->
<style lang="stylus" rel="stylesheet/stylus" type="text/stylus">
    /*单位转换*/
    px2rem($px)
        return ($px / 37.5) rem

    ::-webkit-scrollbar
        width 0
        height 0

    @media (-webkit-min-device-pixel-ratio: 1.5), (min-device-pixel-ratio: 1.5)
        .border-1px
            &::after
                -webkit-transform scaleY(0.7)
                transform scaleY(0.7)
            &::before
                -webkit-transform scaleY(0.7)
                transform scaleY(0.7)

    @media (-webkit-min-device-pixel-ratio: 2), (min-device-pixel-ratio: 2)
        .border-1px
            &::after
                -webkit-transform scaleY(0.5)
                transform scaleY(0.5)
            &::before
                -webkit-transform scaleY(0.5)
                transform scaleY(0.5)

    .cell-box
        display flex
        position relative
        height px2rem(48px)
        line-height px2rem(48px)
        &::after
            position absolute
            left 0
            bottom 0
            display block
            width 100%
            border-bottom px2rem(1px) solid rgba(0, 0, 0, .1)
            content ''
        .cell-box-title
            flex 1
            overflow hidden
            text-overflow ellipsis
            white-space nowrap
            padding-left px2rem(10px)
        .cell-box-des
            width px2rem(100px)
